INSERT INTO `mybb_templates` (`tid`, `title`, `template`, `sid`, `version`, `status`, `dateline`) VALUES
(1, 'announcement', '<html>\n<head>\n<title>{$lang->forum_announcement}</title>\n{$headerinclude}\n<script type=\"text/javascript\">\nvar announcement_quickdelete_confirm = \"{$lang->announcement_quickdelete_confirm}\";\n</script>\n</head>\n<body>\n{$header}\n<table border=\"0\" cellspacing=\"{$theme[\'borderwidth\']}\" cellpadding=\"{$theme[\'tablespace\']}\" class=\"tborder tfixed clear no_bottom_border\">\n<tr>\n<td class=\"thead\"><strong>{$lang->forum_announcement}</strong></td>\n</tr>\n<tr><td id=\"posts_container\">\n	<div id=\"posts\">\n	{$announcement}\n	</div>\n</td></tr>\n</table>\n{$footer}\n</body>\n</html>', -2, '1820', '', 1734385878),
(2, 'announcement_edit', '<a href=\"modcp.php?action=edit_announcement&amp;aid={$post[\'aid\']}\" title=\"{$lang->announcement_edit}\" class=\"postbit_edit\"><span>{$lang->postbit_button_edit}</span></a>', -2, '1800', '', 1734385878),
(3, 'announcement_quickdelete', '<a href=\"modcp.php?action=delete_announcement&amp;aid={$post[\'aid\']}&amp;my_post_key={$mybb->post_code}\" onclick=\"MyBB.deleteAnnouncement(this); return false;\" class=\"postbit_qdelete\"><span>{$lang->postbit_button_qdelete}</span></a>', -2, '1800', '', 1734385878),
