(1226, 'modcp_editprofile', '<html>\n<head>\n<title>{$mybb->settings[\'bbname\']} - {$lang->edit_profile}</title>\n{$headerinclude}\n<script type=\"text/javascript\">\n<!--\n	var mod_posts = \'{$modpost_check}\';\n	var susp_posts = \'{$suspost_check}\';\n// -->\n</script>\n</head>\n<body>\n	{$header}\n	<form action=\"modcp.php\" method=\"post\">\n		<input type=\"hidden\" name=\"my_post_key\" value=\"{$mybb->post_code}\" />\n			<div class=\"container-md\">\n		\n		<div class=\"row\">\n			\n			<div class=\"col-lg-3\">\n\n{$modcp_nav}\n				\n				</div>\n			<div class=\"col\">\n					{$errors}\n				\n				<div class=\"card mb-4\">\n	<div class=\"card-footer border-top-0 border-bottom text-19 fw-bold\">\n		{$lang->edit_profile}\n	</div>\n	<div class=\"card-body pt-1\">	\n		\n		<div class=\"bg-nav p-2 rounded text-16 d-block d-sm-block d-md-block d-lg-none mb-3\">{$lang->profile_optional}</div>\n<div class=\"row g-3 m-auto border-bottom pb-4 pt-0 mt-0 mb-2\">\n<div class=\"col-lg-3 d-none d-sm-none d-md-none d-lg-block text-center text-sm-center text-md-center text-lg-end border-end text-16 fw-bold pe-3 me-3\">\n{$lang->profile_optional}\n</div>\n<div class=\"col\">\n{$requiredfields}\n\n\n		<input type=\"checkbox\" class=\"form-check-input\" name=\"remove_avatar\" value=\"1\" />\n  <label class=\"form-check-label\" for=\"remove_avatar\">\n	  {$lang->remove_avatar}</label>\n	\n	\n	\n</div>\n</div>\n	\n<div class=\"bg-nav p-2 rounded text-16 d-block d-sm-block d-md-block d-lg-none mb-3\">{$lang->birthday}</div>\n<div class=\"row g-3 m-auto border-bottom pb-4 pt-0 mt-0 mb-2\">\n<div class=\"col-lg-3 d-none d-sm-none d-md-none d-lg-block text-center text-sm-center text-md-center text-lg-end border-end text-16 fw-bold pe-3 me-3\">\n{$lang->birthday}\n</div>\n<div class=\"col\">\n<div class=\"alert bg-nav mb-0\">\n				<div class=\"row g-1\">\n					<div class=\"col-auto\">\n						<span class=\"small text-desc\">{$lang->day}</span>\n<select name=\"birthday_day\" class=\"form-select form-select-sm border pe-5\">\n<option value=\"\">&nbsp;</option>\n{$bdaydaysel}\n</select>\n					</div>\n					<div class=\"col-auto\">\n						<span class=\"small text-desc\">{$lang->month}</span>\n<select name=\"birthday_month\" class=\"form-select form-select-sm border pe-5\">\n<option value=\"\">&nbsp;</option>\n<option value=\"1\" {$bdaymonthsel[\'1\']}>{$lang->month_1}</option>\n<option value=\"2\" {$bdaymonthsel[\'2\']}>{$lang->month_2}</option>\n<option value=\"3\" {$bdaymonthsel[\'3\']}>{$lang->month_3}</option>\n<option value=\"4\" {$bdaymonthsel[\'4\']}>{$lang->month_4}</option>\n<option value=\"5\" {$bdaymonthsel[\'5\']}>{$lang->month_5}</option>\n<option value=\"6\" {$bdaymonthsel[\'6\']}>{$lang->month_6}</option>\n<option value=\"7\" {$bdaymonthsel[\'7\']}>{$lang->month_7}</option>\n<option value=\"8\" {$bdaymonthsel[\'8\']}>{$lang->month_8}</option>\n<option value=\"9\" {$bdaymonthsel[\'9\']}>{$lang->month_9}</option>\n<option value=\"10\" {$bdaymonthsel[\'10\']}>{$lang->month_10}</option>\n<option value=\"11\" {$bdaymonthsel[\'11\']}>{$lang->month_11}</option>\n<option value=\"12\" {$bdaymonthsel[\'12\']}>{$lang->month_12}</option>\n</select>\n					</div>\n	<div class=\"col-auto\">\n						<span class=\"small text-desc\">{$lang->year}</span>\n						<input type=\"text\" class=\"form-control form-control-sm border w-auto\" size=\"5\" maxlength=\"4\" name=\"birthday_year\" value=\"{$birthday_year}\" />\n					</div>\n				</div>\n	\n	</div>\n</div>\n</div>	\n\n	<div class=\"bg-nav p-2 rounded text-16 d-block d-sm-block d-md-block d-lg-none mb-3\">{$lang->website_url}</div>\n<div class=\"row g-3 m-auto border-bottom pb-4 pt-0 mt-0 mb-2\">\n<div class=\"col-lg-3 d-none d-sm-none d-md-none d-lg-block text-center text-sm-center text-md-center text-lg-end border-end text-16 fw-bold pe-3 me-3\">\n			{$lang->website_url}\n			\n		</div>\n		<div class=\"col\">\n			\n<input type=\"text\" class=\"form-control form-control-sm border\" name=\"website\" maxlength=\"75\" value=\"{$user[\'website\']}\" />\n		</div>\n		\n		</div>	\n{$customfields}\n						\n		\n		<div class=\"bg-nav p-2 rounded text-16 d-block d-sm-block d-md-block d-lg-none mb-3\">{$lang->custom_usertitle}</div>\n	<div class=\"row g-3 m-auto border-bottom pb-4 pt-0 mb-2\">\n		<div class=\"col-lg-3 d-none d-sm-none d-md-none d-lg-block text-center text-sm-center text-md-center text-lg-end border-end text-16 fw-bold pe-3 me-3\">\n			{$lang->custom_usertitle}\n			\n		</div>\n		<div class=\"col\">\n			<span class=\"text-desc\">{$lang->custom_usertitle_note}</span>\n			\n			<div class=\"fw-bold fs-6 mb-3 mt-3 pb-3 border-bottom\">{$lang->default_usertitle} {$defaulttitle}</div>\n			\n			\n			\n			{$currentcustom}\n			\n			<label for=\"newtitle\">{$lang->new_custom_usertitle}</label>\n	<input type=\"text\" class=\"form-control form-control-sm border\" name=\"usertitle\" maxlength=\"{$mybb->settings[\'customtitlemaxlength\']}\" value=\"{$newtitle}\" />\n			<input type=\"checkbox\" name=\"reverttitle\" id=\"reverttitle\" class=\"form-check-input\" /> {$lang->revert_usertitle}\n			\n		</div>\n		\n		</div>\n	\n\n<div class=\"bg-nav p-2 rounded text-16 d-block d-sm-block d-md-block d-lg-none mb-3\">{$lang->additional_contact_details}</div>\n<div class=\"row g-3 m-auto border-bottom pb-4 pt-0 mt-0 mb-2\">\n<div class=\"col-lg-3 d-none d-sm-none d-md-none d-lg-block text-center text-sm-center text-md-center text-lg-end border-end text-16 fw-bold pe-3 me-3\">\n{$lang->additional_contact_details}\n</div>\n<div class=\"col\">\n<div class=\"mb-3 pb-3 border-bottom\">\n		<label for=\"icq\">{$lang->icq_number}</label>\n		<input type=\"text\" class=\"form-control border form-control-sm\" name=\"icq\" size=\"25\" value=\"{$user_icq}\" />\n	</div>\n	<div class=\"mb-3 pb-3 border-bottom\">\n		<label for=\"skype\">{$lang->skype_id}</label>\n		<input type=\"text\" class=\"form-control border form-control-sm\" name=\"skype\" size=\"25\" value=\"{$user_skype}\" />\n	</div>\n	\n		<label for=\"google\">{$lang->google_id}</label>\n		<input type=\"text\" class=\"form-control border form-control-sm\" name=\"google\" size=\"25\" value=\"{$user_google}\" />\n	\n</div>\n</div>\n		\n\n<div class=\"bg-nav p-2 rounded text-16 d-block d-sm-block d-md-block d-lg-none mb-3\">{$lang->moderation}</div>\n<div class=\"row g-3 m-auto border-bottom pb-4 pt-0 mt-0 mb-2\">\n<div class=\"col-lg-3 d-none d-sm-none d-md-none d-lg-block text-center text-sm-center text-md-center text-lg-end border-end text-16 fw-bold pe-3 me-3\">\n{$lang->moderation}\n</div>\n<div class=\"col\">\n<input type=\"checkbox\" name=\"moderateposting\" value=\"1\" {$modpost_checked} class=\"form-check-input\" onclick=\"toggleSuspend(\'modposts\');\" id=\"modpost\" /> {$lang->moderate_posts} <br />\n\n			<div id=\"modposts_action\" class=\"mt-2 mb-2\">\n{$moderateposts_info}\n			\n{$lang->modpost_length}\n				<div class=\"row\">\n					<div class=\"col align-self-center\">\n<input type=\"text\" name=\"modpost_time\" value=\"{$modpost_time}\" class=\"form-control border form-control-sm w-100\" />\n					</div>\n					<div class=\"col align-self-center\">\n						{$modpost_options}\n					</div>\n				</div>\n			</div>\n\n<input type=\"checkbox\" name=\"suspendposting\" value=\"1\" {$suspost_checked} class=\"form-check-input\" onclick=\"toggleSuspend(\'susposts\');\" id=\"suspost\" /> {$lang->suspend_posts}\n			\n			\n<div id=\"susposts_action\" class=\"mt-2\">\n\n{$suspendposting_info}\n\n{$lang->suspost_length}\n	<div class=\"row\">\n					<div class=\"col align-self-center\">\n<input type=\"text\" name=\"suspost_time\" value=\"{$suspost_time}\" class=\"form-control border form-control-sm w-100\" />\n					</div>\n					<div class=\"col align-self-center\">\n						{$suspost_options}\n					</div>\n				</div>\n			</div>\n</div>\n</div>\n{$awaysection}\n\n					</div>			\n<div class=\"card mb-4\">\n	<div class=\"card-footer border-top-0 border-bottom text-19 fw-bold\">\n		{$lang->signature}\n	</div>\n	<div class=\"card-body\">				\n	<textarea rows=\"15\" cols=\"70\" id=\"signature\" style=\"height: 250px\" class=\"form-control border form-control-sm\" name=\"signature\">{$user[\'signature\']}</textarea>\n{$codebuttons}\n\n{$suspend_signature}\n	</div>\n				</div>\n				\n<div class=\"card mb-4\">\n	<div class=\"card-footer border-top-0 border-bottom text-19 fw-bold\">\n		{$lang->mod_notes}\n	</div>\n	<div class=\"card-body\">				\n	<textarea name=\"usernotes\" id=\"usernotes\" rows=\"10\" cols=\"70\" class=\"form-control border form-control-sm\">{$user[\'usernotes\']}</textarea>\n	</div>\n	\n	<div class=\"card-footer text-center\">\n						<input type=\"hidden\" name=\"action\" value=\"do_editprofile\" />\n						<input type=\"hidden\" name=\"uid\" value=\"{$user[\'uid\']}\" />\n						<button type=\"submit\" class=\"btn btn-primary\" value=\"{$lang->update_profile}\"><i class=\"fa-solid fa-sliders\"></i> &nbsp;{$lang->update_profile}</button>		\n	</div>\n				</div>				\n					\n\n					\n						\n	</form>\n	</div></div></div>\n\n	{$footer}\n<script type=\"text/javascript\">\n<!--\n	if(mod_posts == 0)\n	{\n		$(\"#modposts_action\").hide();\n	}\n\n	if(susp_posts == 0)\n	{\n		$(\"#susposts_action\").hide();\n	}\n\n	function toggleSuspend(action)\n	{\n		if(action == \"modposts\")\n		{\n			$(\"#susposts_action\").hide();\n			$(\"#suspost\").attr(\"checked\", false);\n\n			if($(\"#modpost\").prop(\"checked\") == false)\n			{\n				$(\"#modpost\").attr(\"checked\", false);\n				$(\"#modposts_action\").hide();\n			}\n			else\n			{\n				$(\"#modpost\").attr(\"checked\", true);\n				$(\"#modposts_action\").show();\n			}\n		}\n\n		if(action == \"susposts\")\n		{\n			$(\"#modposts_action\").hide();\n			$(\"#modpost\").attr(\"checked\", false);\n\n			if($(\"#suspost\").prop(\"checked\") == false)\n			{\n				$(\"#suspost\").attr(\"checked\", false);\n				$(\"#susposts_action\").hide();\n			}\n			else\n			{\n				$(\"#suspost\").attr(\"checked\", true);\n				$(\"#susposts_action\").show();\n			}\n		}\n	}\n// -->\n</script>\n</body>\n</html>', 2, '1837', '', 1734385989),
