(1228, 'modcp_editprofile_signature', '<script type=\"text/javascript\">\n<!--\n	var sig_checked = \"{$checked}\";\n\n	function toggleAction()\n	{\n		if($(\"#suspend_action\").is(\":visible\") == true)\n		{\n			$(\"#suspend_action\").hide();\n		}\n		else\n		{\n			$(\"#suspend_action\").show();\n		}\n	}\n// -->\n</script>\n<input type=\"checkbox\" name=\"suspendsignature\" value=\"1\" class=\"form-check-input\" {$checked_item} onclick=\"toggleAction();\" /> <span class=\"fw-normal\">{$lang->suspend_signature}</span>\n\n<div class=\"mt-2\" id=\"suspend_action\">\n{$suspendsignature_info}\n	\n{$lang->suspend_length}	\n<div class=\"row\">\n					<div class=\"col align-self-center\">\n<input type=\"text\" name=\"action_time\" value=\"{$action_time}\" class=\"form-control border form-control-sm w-100\" />\n					</div>\n					<div class=\"col align-self-center\">\n						{$action_options}\n					</div>\n				</div>\n</div>\n<script type=\"text/javascript\">\n<!--\n	if(sig_checked == 0)\n	{\n		$(\"#suspend_action\").hide();\n	}\n// -->\n</script>', 2, '1837', '', 1734385989),
