(1230, 'modcp_finduser', '<html>\n<head>\n	<title>{$mybb->settings[\'bbname\']} - {$lang->find_users}</title>\n{$headerinclude}\n</head>\n<body>\n	{$header}\n		<div class=\"container-md\">\n		\n		<div class=\"row\">\n			\n			<div class=\"col-lg-3\">\n\n{$modcp_nav}\n			</div>\n			<div class=\"col\">\n\n\n<div class=\"card mb-4\">\n	<div class=\"card-footer border-top-0 border-bottom text-19 fw-bold\">\n		{$lang->users}\n	</div>\n	<div class=\"card-body pt-2 pb-0\">\n		\n		<div class=\"d-none d-sm-none d-md-none d-lg-block d-xl-block d-xxl-block\">\n		<div class=\"row pb-2 fw-bold\">\n			<div class=\"col\">\n				{$lang->username}\n			</div>\n			<div class=\"col\">\n				{$lang->usergroup}\n			</div>\n			<div class=\"col\">\n				{$lang->regdate}\n			</div>\n			<div class=\"col\">\n				{$lang->lastvisit}\n			</div>\n			<div class=\"col\">\n				{$lang->postnum}\n			</div>\n		</div>\n		</div>\n{$users}\n	</div>\n				</div>\n				\n				\n<div class=\"card mb-4\">\n	<div class=\"card-footer border-top-0 border-bottom text-19 fw-bold\">\n		{$lang->find_users}\n	</div>\n	<div class=\"card-body pt-0\">					\n	<form action=\"modcp.php?action=finduser\" method=\"post\">\n<div class=\"bg-nav p-2 rounded text-16 d-block d-sm-block d-md-block d-lg-none mb-3\">{$lang->username_contains}</div>\n<div class=\"row g-3 m-auto border-bottom pb-4 pt-0 mt-0 mb-2\">\n<div class=\"col-lg-3 d-none d-sm-none d-md-none d-lg-block text-center text-sm-center text-md-center text-lg-end border-end text-16 fw-bold pe-3 me-3\">\n{$lang->username_contains}\n</div>\n<div class=\"col\">\n<input type=\"text\" name=\"username\" id=\"username\" value=\"{$username}\" class=\"form-control border form-control-sm\" />\n</div>\n</div>					\n			\n<div class=\"bg-nav p-2 rounded text-16 d-block d-sm-block d-md-block d-lg-none mb-3\">{$lang->sort_by}</div>\n<div class=\"row g-3 m-auto pt-0 mt-0 mb-2\">\n<div class=\"col-lg-3 d-none d-sm-none d-md-none d-lg-block text-center text-sm-center text-md-center text-lg-end border-end text-16 fw-bold pe-3 me-3\">\n{$lang->sort_by}\n</div>\n<div class=\"col\">\n<div class=\"pb-3 mb-3 border-bottom\">\n	<select name=\"sortby\" class=\"form-select border form-select-sm w-auto pe-5\">\n									<option value=\"username\"{$sortbysel[\'username\']}>{$lang->username}</option>\n									<option value=\"regdate\"{$sortbysel[\'regdate\']}>{$lang->regdate}</option>\n									<option value=\"lastvisit\"{$sortbysel[\'lastvisit\']}>{$lang->lastvisit}</option>\n									<option value=\"postnum\"{$sortbysel[\'postnum\']}>{$lang->postnum}</option>\n								</select>\n	</div>\n	\n<select name=\"order\" class=\"form-select border form-select-sm pe-5 w-auto\">\n									<option value=\"asc\"{$ordersel[\'asc\']}>{$lang->asc}</option>\n									<option value=\"desc\"{$ordersel[\'desc\']}>{$lang->desc}</option>\n								</select>	\n	\n</div>\n</div>	\n		\n		</div>\n		\n		{$multipage}			\n							\n					<div class=\"card-footer text-center\">\n						<button type=\"submit\" class=\"btn btn-primary\" value=\"{$lang->find_users}\"><i class=\"fa-solid fa-magnifying-glass\"></i> &nbsp;{$lang->find_users}</button>\n					</div>\n				</form>\n				</div>\n	\n</div></div>\n</div>\n{$footer}\n<link rel=\"stylesheet\" href=\"{$mybb->asset_url}/jscripts/select2/select2-2.css?ver=1807\" />\n<script type=\"text/javascript\" src=\"{$mybb->asset_url}/jscripts/select2/select2.min.js?ver=1806\"></script>\n<script type=\"text/javascript\">\n<!--\nif(use_xmlhttprequest == \"1\")\n{\n	MyBB.select2();\n	$(\"#username\").select2({\n		placeholder: \"{$lang->find_users}\",\n		minimumInputLength: 2,\n		multiple: false,\n		allowClear: true,\n		ajax: { // instead of writing the function to execute the request we use Select2\'s convenient helper\n			url: \"xmlhttp.php?action=get_users\",\n			dataType: \'json\',\n			data: function (term, page) {\n				return {\n					query: term, // search term\n				};\n			},\n			results: function (data, page) { // parse the results into the format expected by Select2.\n				// since we are using custom formatting functions we do not need to alter remote JSON data\n				return {results: data};\n			}\n		},\n		initSelection: function(element, callback) {\n			var value = $(element).val();\n			if (value !== \"\") {\n				callback({\n					id: value,\n					text: value\n				});\n			}\n		}\n	});\n}\n// -->\n</script>\n</body>\n</html>', 2, '1837', '', 1734385989),
