(1233, 'modcp_ipsearch', '<html>\n<head>\n	<title>{$mybb->settings[\'bbname\']} - {$lang->ipsearch}</title>\n	{$headerinclude}\n</head>\n<body>\n	{$header}\n		<div class=\"container-md\">\n		\n		<div class=\"row\">\n			\n			<div class=\"col-lg-3\">\n\n{$modcp_nav}\n			</div>\n			<div class=\"col\">\n								{$ipsearch_results}\n				<form action=\"modcp.php?action=ipsearch\" method=\"post\">\n<div class=\"card mb-4\">\n	<div class=\"card-footer border-top-0 border-bottom text-19 fw-bold\">\n		{$lang->ipaddress_search}\n	</div>\n	<div class=\"card-body\">		\n		\n		<label for=\"ipaddress\">{$lang->ip_address}</label>\n	   <input type=\"text\" class=\"form-control border form-control-sm mb-1\" name=\"ipaddress\" value=\"{$ipaddressvalue}\" size=\"30\" />\n						\n	\n	<div class=\"form-check form-check-inline\">\n  <input type=\"checkbox\" class=\"form-check-input\" name=\"search_users\" value=\"1\" {$usersearchselect} />\n  <label class=\"form-check-label\" for=\"inlineCheckbox1\">{$lang->search_users}</label>\n</div>\n<div class=\"form-check form-check-inline\">\n  <input type=\"checkbox\" class=\"form-check-input\" name=\"search_posts\" value=\"1\" {$postsearchselect} />\n  <label class=\"form-check-label\" for=\"inlineCheckbox2\">{$lang->search_posts}</label>\n</div>\n	</div>			\n					<div class=\"card-footer text-center\">\n						<button type=\"submit\" class=\"btn btn-primary\" value=\"{$lang->find}\"><i class=\"fa-solid fa-magnifying-glass\"></i> &nbsp;{$lang->find}</button>\n					</div>\n				</form>\n		</div>\n	</div></div>\n</div></div>\n\n{$footer}\n</body>\n</html>', 2, '1837', '', 1734385989),
