(1288, 'moderation_confirmation', '<html>\n<head>\n<title>{$mybb->settings[\'bbname\']} - {$lang->confirm_execute_tool}</title>\n{$headerinclude}\n</head>\n<body>\n{$header}\n<form action=\"moderation.php\" method=\"post\">\n<input type=\"hidden\" name=\"my_post_key\" value=\"{$mybb->post_code}\" />\n<input type=\"hidden\" name=\"action\" value=\"{$action}\" />\n<input type=\"hidden\" name=\"modtype\" value=\"{$modtype}\" />\n<input type=\"hidden\" name=\"tid\" value=\"{$tid}\" />\n<input type=\"hidden\" name=\"pid\" value=\"{$pid}\" />\n<input type=\"hidden\" name=\"fid\" value=\"{$fid}\" />\n<input type=\"hidden\" name=\"pmid\" value=\"{$pmid}\" />\n<input type=\"hidden\" name=\"confirm\" value=\"1\" />\n<input type=\"hidden\" name=\"inlinetype\" value=\"{$inlinetype}\" />\n<input type=\"hidden\" name=\"searchid\" value=\"{$searchid}\" />\n<input type=\"hidden\" name=\"url\" value=\"{$url}\" />\n	<div class=\"container-md\">\n<div class=\"card\">\n<div class=\"card-body\">\n	<div class=\"legend mb-4\">{$thread[\'subject\']} - {$lang->confirm_execute_tool}</div>\n	<div class=\"ps-3 pe-3\">\n{$loginbox}\n	<div class=\"mt-3 text-muted\">\n		{$lang->confirm_execute_tool_desc}</div>\n\n<div class=\"text-end mt-3\"><input type=\"submit\" class=\"btn btn-primary\" name=\"submit\" value=\"{$lang->confirm_execute_tool}\" /></div>\n		</div>\n</div>\n</div>\n</div>\n</form>\n{$footer}\n</body>\n</html>', 2, '1830', '', 1734385989),
