(1470, 'private_tracking', '<html>\n<head>\n<title>{$mybb->settings[\'bbname\']} - {$lang->pm_tracking}</title>\n{$headerinclude}\n</head>\n<body>\n{$header}\n<div class=\"container-md\">\n<div class=\"row\">\n<div class=\"col-lg-3\">\n\n{$usercpnav}\n				\n</div>\n<div class=\"col\">\n\n	\n	<form action=\"private.php\" method=\"post\">\n<input type=\"hidden\" name=\"my_post_key\" value=\"{$mybb->post_code}\" />\n<input type=\"hidden\" name=\"action\" value=\"do_tracking\" />\n		\n		<div class=\"card border-0 mb-4\">\n<div class=\"card-header py-3 bg-nav rounded border-bottom-0\">\n		\n		<div class=\"row g-2 text-forum\">\n			<div class=\"col-1\">\n				&nbsp;\n			</div>\n			<div class=\"col align-self-center\">\n				{$lang->message_title} &mdash; {$lang->sentto}\n			</div>\n			<div class=\"col-3 align-self-center\">\n				<span class=\"d-none d-sm-none d-md-none d-lg-inline-block d-xl-inline-block d-xxl-inline-block\">{$lang->dateread}</span>\n				<span class=\"float-end\"><input type=\"checkbox\" class=\"form-check-input checkall\" name=\"allbox\" /></span>\n			</div>\n	</div>\n		\n			</div>\n		</div>\n\n{$readmessages}\n\n	<div class=\"card border-0 mb-4\">\n<div class=\"card-header py-3 bg-nav rounded border-bottom-0\">\n{$stoptrackingread}\n</form>\n		</div>\n		</div>\n\n\n	{$unread_multipage}\n		\n<form action=\"private.php\" method=\"post\">\n<input type=\"hidden\" name=\"my_post_key\" value=\"{$mybb->post_code}\" />\n<input type=\"hidden\" name=\"action\" value=\"do_tracking\" />\n	\n	<div class=\"card border-0 mb-4\">\n<div class=\"card-header py-3 bg-nav rounded border-bottom-0\">\n		\n		<div class=\"row g-2 text-forum\">\n			<div class=\"col-1\">\n				&nbsp;\n			</div>\n			<div class=\"col align-self-center\">\n				{$lang->message_title} &mdash; {$lang->sentto}\n			</div>\n			<div class=\"col-3 align-self-center\">\n				<span class=\"d-none d-sm-none d-md-none d-lg-inline-block d-xl-inline-block d-xxl-inline-block\">{$lang->datesent}</span>\n				<span class