(1477, 'report', '<div class=\"modal p-0 m-0\">\n	  <form action=\"report.php\" method=\"post\" class=\"reportData_{$id}\" onsubmit=\"return Report.submitReport({$id});\">\n<input type=\"hidden\" name=\"my_post_key\" value=\"{$mybb->post_code}\" />\n<input type=\"hidden\" name=\"action\" value=\"do_report\" />\n<input type=\"hidden\" name=\"type\" value=\"{$report_type}\" />\n<input type=\"hidden\" name=\"pid\" value=\"{$id}\" />\n<input type=\"hidden\" name=\"no_modal\" value=\"1\" />\n		<div class=\"card border modal_{$id}\" style=\"overflow-y: auto; max-height: 500px;\">\n			<div class=\"card-header text-19 fw-bold border-0 py-3\"><i class=\"fa-solid fa-flag\"></i></div>\n			<div class=\"card-body\">{$report_reasons}\n			</form>\n			\n			<script type=\"text/javascript\">\n<!--\n	if($(\"#report_reason option:selected\").attr(\'data-extra\') != 1)\n	{\n		$(\"#reason\").hide();\n	}\n\n	$(\"#report_reason\").on(\'change\', function()\n	{\n		if($(this).find(\"option:selected\").attr(\'data-extra\') == 1)\n		{\n			$(\"#reason\").show();\n			return;\n		}\n\n		$(\"#reason\").hide();\n	});\n// -->\n</script>\n	</div>\n</div>', 2, '1837', '', 1734385989),
