(1482, 'reputation', '<html>\n<head>\n<title>{$mybb->settings[\'bbname\']} - {$lang->reputation_report}</title>\n{$headerinclude}\n<script type=\"text/javascript\">\n<!--\n	var delete_reputation_confirm = \"{$lang->delete_reputation_confirm}\";\n// -->\n</script>\n<script type=\"text/javascript\" src=\"{$mybb->asset_url}/jscripts/report.js?ver=1820\"></script>\n</head>\n<body>\n{$header}\n	<div class=\"container-md\">\n		<div class=\"text-end mb-2\">\n			{$add_reputation}\n		</div>\n<div class=\"card mb-4\">\n<div class=\"card-body\">\n	\n	<div class=\"row g-2\">\n		<div class=\"col-lg-7 align-self-center\">\n			<h4 class=\"mb-0\">{$username}</h4>\n			{$usertitle}\n					<br /><br />\n					<span class=\"text-muted\">{$lang->total_reputation}:</span> {$rep_total}<br />\n					<span class=\"text-muted\">{$lang->reputation_members} {$rep_members}</span><br />\n					<span class=\"text-muted\">{$lang->reputation_posts} {$rep_posts}</span>\n			\n			<hr class=\"d-block d-sm-block d-md-block d-lg-none d-xl-none d-xxl-none\" />\n		</div>\n		<div class=\"col-lg-5\">\n<div class=\"row pb-2 text-center border-bottom\">\n			<div class=\"col align-self-center\">\n				&nbsp;\n			</div>\n			<div class=\"col align-self-center\">\n				{$lang->positive_count}\n			</div>\n			<div class=\"col align-self-center\">\n				{$lang->neutral_count}\n			</div>\n			<div class=\"col align-self-center\">\n				{$lang->negative_count}\n			</div>\n		</div>\n		\n		<div class=\"row py-2 border-bottom\">\n			<div class=\"col text-muted align-self-center\">\n				{$lang->last_week}\n			</div>\n			<div class=\"col align-self-center text-center\">\n				{$f_positive_week}\n			</div>\n			<div class=\"col align-self-center text-center\">\n				{$f_neutral_week}\n			</div>\n			<div class=\"col align-self-center text-center\">\n				{$f_negative_week}\n			</div>\n		</div>\n		\n		<div class=\"row py-2 border-bottom\">\n			<div class=\"col text-muted align-self-center\">\n				{$lang->last_month}\n			</div>\n			<div class=\"col align-self-center text-center\">\n				{$f_positive_month}\n			</div>\n			<div class=\"col align-self-center text-center\">\n				{$f_neutral_month}\n			</div>\n			<div class=\"col align-self-center text-center\">\n				{$f_negative_month}\n			</div>\n		</div>\n		\n		<div class=\"row py-2 border-bottom\">\n			<div class=\"col text-muted align-self-center\">\n				{$lang->last_6months}\n			</div>\n			<div class=\"col align-self-center text-center\">\n				{$f_positive_6months}\n			</div>\n			<div class=\"col align-self-center text-center\">\n				{$f_neutral_6months}\n			</div>\n			<div class=\"col align-self-center text-center\">\n				{$f_negative_6months}\n			</div>\n		</div>\n		\n		<div class=\"row py-2\">\n			<div class=\"col text-muted align-self-center\">\n				{$lang->all_time}\n			</div>\n			<div class=\"col align-self-center text-center\">\n				{$f_positive_count}\n			</div>\n			<div class=\"col align-self-center text-center\">\n				{$f_neutral_count}\n			</div>\n			<div class=\"col align-self-center text-center\">\n				{$f_negative_count}\n			</div>\n		</div>\n	</div>\n	</div>\n	</div>\n		</div>\n	\n	<div class=\"card border-0 mb-2\">\n	<div class=\"card-header rounded-bottom text-19 fw-bold\">\n		{$lang->comments}\n	</div>\n		</div>\n\n{$reputation_votes}\n		\n		\n		<div class=\"card-footer bg-nav border-0 rounded mt-2 text-center\">\n\n		<div class=\"row g-2\">\n			<div class=\"col d-none d-sm-none d-md-none d-lg-block d-xl-block d-xxl-block\">\n				&nbsp;\n			</div>\n			<div class=\"col-lg-auto\">\n	<form action=\"reputation.php\" method=\"get\">\n		<input type=\"hidden\" name=\"uid\" value=\"{$user[\'uid\']}\" />\n		<select name=\"show\" class=\"form-select form-select-sm border w-auto pe-5\">\n			<option value=\"all\" {$show_selected[\'all\']}>{$lang->show_all}</option>\n			<option value=\"positive\" {$show_selected[\'positive\']}>{$lang->show_positive}</option>\n			<option value=\"neutral\" {$show_selected[\'neutral\']}>{$lang->show_neutral}</option>\n			<option value=\"negative\" {$show_selected[\'negative\']}>{$lang->show_negative}</option>\n		</select>\n		</div>\n				<div class=\"col-lg-auto\">\n		<select name=\"sort\" class=\"form-select form-select-sm border w-auto pe-5\">\n			<option value=\"dateline\" {$sort_selected[\'last_updated\']}>{$lang->sort_updated}</option>\n			<option value=\"username\" {$sort_selected[\'username\']}>{$lang->sort_username}</option>\n		</select>\n				</div>\n				<div class=\"col-auto\">\n		{$gobutton}\n				</div>\n							<div class=\"col d-none d-sm-none d-md-none d-lg-block d-xl-block d-xxl-block\">\n				&nbsp;\n			</div>\n			</div>\n	</form>\n	\n	\n	\n	</div></div></div>\n{$multipage}\n\n	\n{$footer}\n</body>\n</html>', 2, '1837', '', 1734385989),
(1483, 'reputation_add', '<div class=\"modal bg-white p-5 shadow-sm border-0 rounded\">\n	<div style=\"overflow-y: auto; max-height: 500px;\" class=\"modal_{$user[\'uid\']}_{$reputation_pid}\">\n    \n			<strong>{$vote_title}</strong><br />{$post_rep_info}<br />\n          <form action=\"reputation.php\" method=\"post\" class=\"reputation_{$user[\'uid\']}_{$reputation_pid}\" onsubmit=\"return MyBB.submitReputation({$user[\'uid\']}, {$reputation_pid});\">\n				<input type=\"hidden\" name=\"my_post_key\" value=\"{$mybb->post_code}\" />\n				<input type=\"hidden\" name=\"action\" value=\"do_add\" />\n				<input type=\"hidden\" name=\"uid\" value=\"{$user[\'uid\']}\" />\n				<input type=\"hidden\" name=\"pid\" value=\"{$reputation_pid}\" />\n				<input type=\"hidden\" name=\"rid\" value=\"{$rid}\" />\n				<input type=\"hidden\" name=\"nomodal\" value=\"1\" />\n				<select name=\"reputation\" id=\"reputation\" class=\"form-select border form-select-sm\">\n					{$positive_power}\n					{$neutral_power}\n					{$negative_power}\n				</select>\n				<br /><br />\n				<span class=\"text-muted\">{$lang->user_comments}</span>\n				<br />\n				<input type=\"text\" class=\"form-control border form-control-sm\" name=\"comments\" maxlength=\"250\" value=\"{$comments}\" />\n				<br />\n				<div style=\"text-align: center;\">\n					<input type=\"submit\" class=\"btn btn-primary\" value=\"{$vote_button}\" />\n					{$delete_button}\n				</div>\n			</form>\n	\n  </div>\n</div>', 2, '1837', '', 1734385989),
