(1525, 'showthread_quickreply', '<div id=\"quickreply_spinner\" class=\"showthread_spinner\" style=\"display: none\"><img src=\"{$theme[\'imgdir\']}/spinner.gif\" /></div>\n\n{$moderation_notice}\n<form method=\"post\" action=\"newreply.php?tid={$tid}&amp;processed=1\" name=\"quick_reply_form\" id=\"quick_reply_form\">\n	<input type=\"hidden\" name=\"my_post_key\" value=\"{$mybb->post_code}\" />\n	<input type=\"hidden\" name=\"subject\" value=\"RE: {$reply_subject}\" />\n	<input type=\"hidden\" name=\"action\" value=\"do_newreply\" />\n	<input type=\"hidden\" name=\"posthash\" value=\"{$posthash}\" id=\"posthash\" />\n	<input type=\"hidden\" name=\"quoted_ids\" value=\"\" id=\"quoted_ids\" />\n	<input type=\"hidden\" name=\"lastpid\" id=\"lastpid\" value=\"{$last_pid}\" />\n	<input type=\"hidden\" name=\"from_page\" value=\"{$page}\" />\n	<input type=\"hidden\" name=\"tid\" value=\"{$tid}\" />\n	<input type=\"hidden\" name=\"method\" value=\"quickreply\" />\n	\n	\n	\n<div class=\"row d-flex g-2 mb-4 mt-5\">\n    <div class=\"col-auto d-none d-sm-none d-md-none d-lg-block d-xl-block d-xxl-block\"><img src=\"{$mybb->user[\'avatar\']}\" class=\"rounded d-none d-sm-none d-md-none d-lg-block d-xl-block d-xxl-block img-fluid\" style=\"width: 100px; height: 100px\"></div>\n    <div class=\"col\">\n		<div class=\"editor_control_bar mb-3 border border-top-5 p-2\" id=\"quickreply_multiquote\" style=\"display: none;\">\n					{$lang->quickreply_multiquote_selected} <a href=\"./newreply.php?tid={$tid}&amp;load_all_quotes=1\" onclick=\"return Thread.loadMultiQuoted();\">{$lang->quickreply_multiquote_now}</a> {$lang->or} <a href=\"javascript:void(0)\" onclick=\"Thread.clearMultiQuoted(); return false;\">{$lang->quickreply_multiquote_deselect}</a>.\n		</div>\n        <div class=\"card\">\n            <div class=\"card-body\">\n				<h6 class=\"mb-2\">{$mybb->user[\'username\']}</h6>\n		<textarea class=\"form-control form-control-sm border-0 p-0\" style=\"resize: none; height: 150px;\"  name=\"message\" id=\"message\" tabindex=\"1\" placeholder=\"Write a reply to this message...\"></textarea>\n			\n				\n	<div id=\"collapse-reply\" class=\"collapse bg-nav p-2\">\n{$option_signature}\n<input type=\"checkbox\" class=\"form-check-input\" name=\"postoptions[disablesmilies]\" value=\"1\" /> {$lang->disable_smilies}<br />\n{$closeoption}\n </div>\n				</div>\n			\n				<div class=\"card-footer border-top-0\">\n					<button type=\"submit\" class=\"btn btn-primary\" value=\"{$lang->post_reply}\" tabindex=\"2\" accesskey=\"s\" id=\"quick_reply_submit\"><i class=\"fa-solid fa-comment\"></i> &nbsp;{$lang->post_reply}</button> <a class=\"btn btn-thread ms-3 me-3\" data-bs-toggle=\"collapse\" aria-expanded=\"false\" aria-controls=\"collapse-1\" href=\"#collapse-reply\" role=\"button\"><i class=\"fa-solid fa-gear\"></i></a> <button type=\"submit\" class=\"btn btn-thread\" name=\"previewpost\" value=\"{$lang->preview_post}\" tabindex=\"3\"><i class=\"fa-solid fa-pencil\"></i> &nbsp;{$lang->preview_post}</button>\n				</div>\n			</div>\n	</div>\n	</div>\n	</div>\n\n	\n</form>', 2, '1837', '', 1734385989),
