(1543, 'stats', '<html>\n<head>\n<title>{$mybb->settings[\'bbname\']} - {$lang->board_stats}</title>\n{$headerinclude}\n</head>\n<body>\n{$header}\n	<div class=\"container-md\">\n		\n		<div class=\"row\">\n			<div class=\"col-lg-9\">\n				\n\n				\n					<div class=\"card border-0 mb-4\">\n	<div class=\"card-header rounded-bottom text-19 fw-bold mb-0\">\n		{$lang->most_replied_threads}\n	</div>\n	<div class=\"card-body\">\n				{$mostreplies}\n						</div>\n				</div>\n				\n				<div class=\"card border-0 mb-4\">\n	<div class=\"card-header rounded-bottom text-19 fw-bold\">\n		{$lang->most_viewed_threads}\n	</div>\n	<div class=\"card-body\">\n				{$mostviews}\n						</div>\n				</div>\n				\n			</div>\n			<div class=\"col\">\n				\n				\n			<div class=\"card mb-4\">\n	<div class=\"card-header bg-white text-dark border-bottom-0 text-19 fw-bold mt-2 pb-0\">\n		{$lang->totals}\n	</div>\n	<div class=\"card-body\">	\n				<div class=\"py-2 border-bottom\">\n					<span class=\"text-muted\">{$lang->posts}</span> {$stats[\'numposts\']}\n                </div>\n				<div class=\"py-2 border-bottom\">\n					<span class=\"text-muted\">{$lang->threads}</span> {$stats[\'numthreads\']}\n                </div>				\n				<div class=\"py-2\">\n					<span class=\"text-muted\">{$lang->members} </span>{$stats[\'numusers\']}\n                </div>				\n				\n				</div>\n				</div>\n				\n						<div class=\"card mb-4\">\n	<div class=\"card-header bg-white text-dark border-bottom-0 text-19 fw-bold mt-2 pb-0\">\n		{$lang->averages}\n	</div>\n	<div class=\"card-body\">	\n				<div class=\"py-2 border-bottom\">\n					<span class=\"text-muted\">{$lang->ppd}</span> {$postsperday}\n                </div>\n				<div class=\"py-2 border-bottom\">\n					<span class=\"text-muted\">{$lang->tpd}</span> {$threadsperday}\n                </div>				\n				<div class=\"py-2 border-bottom\">\n					<span class=\"text-muted\">{$lang->mpd} </span>{$membersperday}\n                </div>		\n				<div class=\"py-2 border-bottom\">\n					<span class=\"text-muted\">{$lang->ppm}</span> {$postspermember}\n                </div>\n				<div class=\"py-2 border-bottom\">\n					<span class=\"text-muted\">{$lang->tpm}</span> {$threadspermember}\n                </div>				\n				<div class=\"py-2\">\n					<span class=\"text-muted\">{$lang->rpt} </span>{$repliesperthread}\n                </div>			\n				\n				</div>\n				</div>\n				\n				\n					<div class=\"card mb-4\">\n	<div class=\"card-header bg-white text-dark border-bottom-0 text-19 fw-bold mt-2 pb-0\">\n		{$lang->averages}\n	</div>\n	<div class=\"card-body\">	\n								<div class=\"py-2 border-bottom\">\n					<span class=\"text-muted\">{$lang->newest_member} </span>{$stats[\'newest_user\']}\n                </div>		\n				<div class=\"py-2 border-bottom\">\n					<span class=\"text-muted\">{$lang->members_posted}</span> {$havepostedpercent}\n                </div>\n		<div class=\"py-2 border-bottom\">\n			{$lang->todays_top_poster}\n		</div>\n				<div class=\"py-2 border-bottom\">\n					{$lang->popular_forum}\n                </div>				\n				<div class=\"py-2\">\n					{$top_referrer}\n		</div>\n		\n						</div>\n				</div>\n				\n				\n\n	\n	</div>\n				</div>\n			</div>\n	\n{$footer}\n</body>\n</html>', 2, '1837', '', 1734385989),
