(1545, 'usercp', '<html>\n<head>\n<title>{$mybb->settings[\'bbname\']} - {$lang->user_cp}</title>\n{$headerinclude}\n</head>\n<body>\n{$header}\n	\n<div class=\"container-md\">\n<div class=\"row\">\n<div class=\"col-12 col-sm-12 col-md-12 col-lg-3 col-xl-3 col-xxl-3\">\n\n{$usercpnav}\n				\n</div>\n<div class=\"col\">\n	\n	<div class=\"card mb-4\">\n		<div class=\"card-body\">\n			\n		<div class=\"row\">\n			<div class=\"col-auto\">\n				{$avatar}\n			</div>\n			<div class=\"col\">\n				\n				<div class=\"py-2 border-bottom\">\n				{$username}\n				</div>\n				<div class=\"py-2 border-bottom\">\n				<span class=\"text-muted\">{$lang->postnum}</span> <a href=\"search.php?action=finduser&amp;uid={$mybb->user[\'uid\']}\">{$mybb->user[\'posts\']}</a> {$lang->posts_day}\n				</div>\n			<div class=\"py-2 border-bottom\">\n			<span class=\"text-muted\">{$lang->email}</span> {$mybb->user[\'email\']}\n				</div>\n				<div class=\"py-2 border-bottom\">\n			<span class=\"text-muted\">{$lang->registration_date}</span> {$regdate}\n				</div>\n				<div class=\"py-2 border-bottom\">\n			<span class=\"text-muted\">{$lang->primary_usergroup}</span> {$usergroup}\n				</div>\n			{$referral_info}\n		</div>\n	</div>\n			\n		</div>\n	</div>\n				\n\n							\n{$user_notepad}\n{$latest_warnings}\n							\n</div>\n</div>\n\n			\n</div>\n\n{$footer}\n</body>\n</html>', 2, '1837', '', 1734385989),
(1546, 'usercp_addsubscription_thread', '<html>\n<head>\n<title>{$lang->subscribe_to_thread}</title>\n{$headerinclude}\n</head>\n<body>\n{$header}\n<form action=\"usercp.php\" method=\"post\" name=\"input\">\n<input type=\"hidden\" name=\"my_post_key\" value=\"{$mybb->post_code}\" />\n<input type=\"hidden\" name=\"action\" value=\"do_addsubscription\" />\n<input type=\"hidden\" name=\"tid\" value=\"{$thread[\'tid\']}\" />\n<div class=\"container-md\">\n<div class=\"row\">\n<div class=\"col-3 d-none d-sm-none d-md-none d-lg-block d-xl-block d-xxl-block\">\n\n{$usercpnav}\n				\n</div>\n<div class=\"col\">	\n	<!-- offcanvas -->\n				<div class=\"d-block d-sm-block d-md-block d-lg-none d-xl-none d-xxl-none\">\n				<button class=\"btn btn-primary mb-3\" type=\"button\" data-bs-toggle=\"offcanvas\" data-bs-target=\"#offcanvasExample\" aria-controls=\"offcanvasExample\">\n  <i class=\"fa-solid fa-circle-arrow-left\"></i> &nbsp;{$lang->ucpmenu}\n</button>\n				\n				<div class=\"offcanvas offcanvas-start\" tabindex=\"-1\" id=\"offcanvasExample\" aria-labelledby=\"offcanvasExampleLabel\">\n  <div class=\"offcanvas-header\">\n    <h5 class=\"offcanvas-title\" id=\"offcanvasExampleLabel\">{$lang->ucp_nav_menu}</h5>\n    <button type=\"button\" class=\"btn-close\" data-bs-dismiss=\"offcanvas\" aria-label=\"Close\"></button>\n  </div>\n  <div class=\"offcanvas-body\">\n    <div><ul class=\"list-group list-group-flush\">\n		{$usercpnav}</ul>\n    </div>\n  </div>\n</div>\n				</div>\n				<!-- /offcanvas -->\n<div class=\"card\">\n<div class=\"card-body\">\n	\n	<div class=\"legend mb-4\">{$lang->subscribe_to_thread}</div>\n\n	<div class=\"ps-3 pe-3\"><strong>{$lang->notification_method}</strong></div>\n\n	<div class=\"row ps-3 pe-3 mt-4\">\n		<div class=\"col-auto align-self-top text-center\">\n			\n			<input type=\"radio\" name=\"notification\" id=\"notification_none\" class=\"form-check-input\" value=\"0\" {$notification_none_checked} />\n			\n		</div>\n		<div class=\"col align