(1653, 'warnings_view_revoke', '{$warn_errors}\n\n<form action=\"warnings.php\" method=\"post\" name=\"input\">\n	<input type=\"hidden\" name=\"my_post_key\" value=\"{$mybb->post_code}\" />\n	<input type=\"hidden\" name=\"action\" value=\"do_revoke\" />\n	<input type=\"hidden\" name=\"wid\" value=\"{$warning[\'wid\']}\" />\n	\n	<div class=\"card\">\n	<div class=\"card-footer border-top-0 border-bottom text-19 fw-bold\">\n		{$lang->revoke_warning}\n	</div>\n	<div class=\"card-body\">\n    <div class=\"text-desc mb-3\">{$lang->revoke_warning_desc}</div>\n		\n	\n		<label for=\"reason\">{$lang->reason}</label>\n			<textarea name=\"reason\" class=\"form-control border form-control-sm\" cols=\"60\" rows=\"4\"></textarea>\n		\n		</div>\n	<div class=\"card-footer text-center\"><button type=\"submit\" class=\"btn btn-primary\" value=\"{$lang->revoke_warning}\"><i class=\"fa-solid fa-eject\"></i> &nbsp;{$lang->revoke_warning}</button>\n		</div></div>\n</form>', 2, '1837', '', 1734385989),
