(1656, 'warnings_warn', '<html>\n<head>\n<title>{$mybb->settings[\'bbname\']} - {$lang->warn_user}</title>\n{$headerinclude}\n</head>\n<body>\n{$header}\n	<div class=\"container-md\">\n\n{$existing_warnings}\n{$warn_errors}\n<form action=\"warnings.php\" method=\"post\" name=\"input\">\n	<input type=\"hidden\" name=\"my_post_key\" value=\"{$mybb->post_code}\" />\n	<input type=\"hidden\" name=\"action\" value=\"do_warn\" />\n	<input type=\"hidden\" name=\"uid\" value=\"{$user[\'uid\']}\" />\n\n	<div class=\"card mb-4\">\n	<div class=\"card-footer border-top-0 border-bottom text-19 fw-bold\">\n		{$lang->username} {$user_link}\n	</div>\n	<div class=\"card-body\">\n		\n		<div class=\"text-desc mb-3\">{$lang->warn_user_desc}</div>\n{$post}\n\n		\n		<label for=\"notes\">{$lang->warning_note}</label>\n		<textarea name=\"notes\" class=\"form-control border form-control-sm\" cols=\"60\" rows=\"4\">{$notes}</textarea>\n		\n		<div class=\"mt-3\"></div>\n		{$lang->warning_type}\n			\n				<script type=\"text/javascript\">\n				// <!--\n					function checkType(id)\n					{\n						var checked = \'\';\n\n                      $(\'.types_check\').each(function(e, val)\n						{\n							if($(this).prop(\'checked\') == true)\n							{\n								checked = $(this).val();\n							}\n						});\n						$(\'.types\').each(function(e)\n						{\n							$(this).hide();\n						});\n						if($(\'#type_\'+checked))\n						{\n							$(\'#type_\'+checked).show();\n						}\n					}\n				// -->\n				</script>\n				<dl style=\"margin-top: 0; margin-bottom: 0; width: 100%;\">\n					{$types}\n					{$custom_warning}\n				</dl>\n				<script type=\"text/javascript\">\n				// <!--\n					checkType();\n				// -->\n				</script>\n\n		{$pm_notify}\n\n		</div>\n	<div class=\"card-footer text-center\">\n		<button type=\"submit\" class=\"btn btn-primary\" value=\"{$lang->warn_user}\" /><i class=\"fa-solid fa-triangle-exclamation\"></i> &nbsp;{$lang->warn_user}</button></div>\n		</div></div>\n		\n</form>\n{$footer}\n</body>\n</html>', 2, '1837', '', 1734385989);
